Aave_v2_smart_contract=w3.eth.contract(address=Aave_v2_contract_address,abi=Aave_v2_abi)

Aave_v2_start_block = 11362579
w3_current_block = w3.eth.block_number
step = 50000
Aave_v2_flashloan_logs_simplified = []

timestamp_df = pd.read_csv('/local/scratch/exported/MP_Defi_txs_TY_23/timestamp_20081108.csv')
timestamp_df['real_life_time'] = pd.to_datetime(timestamp_df['timestamp'], unit='s')
timestamp_df['date'] = timestamp_df['real_life_time'].dt.date
timestamp_df = timestamp_df[Aave_v2_start_block:]

for i in tqdm(range(Aave_v2_start_block, w3_current_block + 1, step)):
    Aave_v2_flashloan_logs = Aave_v2_smart_contract.events.FlashLoan().get_logs(fromBlock=i,toBlock=i+step-1)
    for j in range(len(Aave_v2_flashloan_logs)):
        info = {
            'recipient': Aave_v2_flashloan_logs[j]['args']['target'],
            'token': Aave_v2_flashloan_logs[j]['args']['asset'],
            'amount': Aave_v2_flashloan_logs[j]['args']['amount'],
            'fee': Aave_v2_flashloan_logs[j]['args']['premium'],
            'tx_hash': Aave_v2_flashloan_logs[j]['transactionHash'].hex(),
            'logIndex': Aave_v2_flashloan_logs[j]['logIndex'], 
            'transactionIndex': Aave_v2_flashloan_logs[j]['transactionIndex'],
            'block_number': Aave_v2_flashloan_logs[j]['blockNumber'],
            'timestamp': timestamp_df['timestamp'][Aave_v2_flashloan_logs[j]['blockNumber']],
            'date': timestamp_df['date'][Aave_v2_flashloan_logs[j]['blockNumber']]
    }
        Aave_v2_flashloan_logs_simplified.append(info)

Aave_v2_flashloan_logs_df = pd.DataFrame(Aave_v2_flashloan_logs_simplified).sort_values(by='timestamp').reset_index(drop=True)
min_indices = Aave_v2_flashloan_logs_df.groupby('tx_hash')['logIndex'].idxmin()
Aave_v2_flashloan_unique_df = Aave_v2_flashloan_logs_df.loc[min_indices].sort_values(by='timestamp').reset_index(drop=True)
Aave_v2_flashloan_count_df = Aave_v2_flashloan_unique_df.groupby('date').size().reset_index(name='daily_flash_tx_count')

Aave_v2_flashloan_logs_df.to_csv('/local/scratch/exported/MP_Defi_txs_TY_23/guanda/Aave_v2_flashloan_logs.csv', index=False)
Aave_v2_flashloan_unique_df.to_csv('/local/scratch/exported/MP_Defi_txs_TY_23/guanda/Aave_v2_flashloan_unique.csv', index=False)
Aave_v2_flashloan_count_df.to_csv('/local/scratch/exported/MP_Defi_txs_TY_23/guanda/Aave_v2_flashloan_count.csv', index=False)


