Balancer_v2_smart_contract=w3.eth.contract(address=Balancer_v2_contract_address,abi=Balancer_v2_abi)
 
Balancer_v2_start_block = 12272146
w3_current_block = w3.eth.block_number
step = 50000
Balancer_v2_flashloan_logs_simplified = []

timestamp_df = pd.read_csv('/local/scratch/exported/MP_Defi_txs_TY_23/timestamp_18012051.csv')
timestamp_df['real_life_time'] = pd.to_datetime(timestamp_df['timestamp'], unit='s')
timestamp_df['date'] = timestamp_df['real_life_time'].dt.date
timestamp_df = timestamp_df[Balancer_v2_start_block:]

for i in tqdm(range(Balancer_v2_start_block, w3_current_block + 1, step)):
    Balancer_v2_flashloan_logs = Balancer_v2_smart_contract.events.FlashLoan().get_logs(fromBlock=i,toBlock=i+step-1)
    for j in range(len(Balancer_v2_flashloan_logs)):
        info = {
            'recipient': Balancer_v2_flashloan_logs[j]['args']['recipient'],
            'token': Balancer_v2_flashloan_logs[j]['args']['token'],
            'amount': Balancer_v2_flashloan_logs[j]['args']['amount'],
            'fee': Balancer_v2_flashloan_logs[j]['args']['feeAmount'],
            'tx_hash': Balancer_v2_flashloan_logs[j]['transactionHash'].hex(),
            'logIndex': Balancer_v2_flashloan_logs[j]['logIndex'], 
            'transactionIndex': Balancer_v2_flashloan_logs[j]['transactionIndex'],
            'block_number': Balancer_v2_flashloan_logs[j]['blockNumber'],
            'timestamp': timestamp_df['timestamp'][Balancer_v2_flashloan_logs[j]['blockNumber']],
            'date': timestamp_df['date'][Balancer_v2_flashloan_logs[j]['blockNumber']]
    }
        Balancer_v2_flashloan_logs_simplified.append(info)

Balancer_v2_flashloan_logs_df = pd.DataFrame(Balancer_v2_flashloan_logs_simplified).sort_values(by='timestamp').reset_index(drop=True)
min_indices = Balancer_v2_flashloan_logs_df.groupby('tx_hash')['logIndex'].idxmin()
Balancer_v2_flashloan_unique_df = Balancer_v2_flashloan_logs_df.loc[min_indices].sort_values(by='timestamp').reset_index(drop=True)
Balancer_v2_flashloan_count_df = Balancer_v2_flashloan_unique_df.groupby('date').size().reset_index(name='daily_flash_tx_count')

Balancer_v2_flashloan_logs_df.to_csv('/local/scratch/exported/MP_Defi_txs_TY_23/guanda/Balancer_v2_flashloan_logs.csv', index=False)
Balancer_v2_flashloan_unique_df.to_csv('/local/scratch/exported/MP_Defi_txs_TY_23/guanda/Balancer_v2_flashloan_unique.csv', index=False)
Balancer_v2_flashloan_count_df.to_csv('/local/scratch/exported/MP_Defi_txs_TY_23/guanda/Balancer_v2_flashloan_count.csv', index=False)


