# Processing function
def process_flash_logs(i):
    results = []
    start_block = int(Uniswap_v3_Pools_TY_df['block_number'][i])
    end_block = 21089068
    pool_address = convert_to_checksum_address(Uniswap_v3_Pools_TY_df['pool_contract_address'][i])
    contract = w3.eth.contract(address=pool_address, abi=abi)
    logs = contract.events.Flash().get_logs(fromBlock=start_block, toBlock=end_block+1)

    for log in logs:
        args = log['args']
        block_number = log['blockNumber']
        timestamp_info = block_to_timestamp[block_number]

        info = {
            'sender': args['sender'],
            'recipient': args['recipient'],
            'token0_symbol': Uniswap_v3_Pools_TY_df['token0_symbol'][i],
            'token1_symbol': Uniswap_v3_Pools_TY_df['token1_symbol'][i],
            'amount0': args['amount0'],
            'amount1': args['amount1'],
            'paid0': args['paid0'],
            'paid1': args['paid1'],
            'pair_contract_address': pool_address,
            'tx_hash': log['transactionHash'].hex(),
            'logIndex': log['logIndex'],
            'transactionIndex': log['transactionIndex'],
            'block_number': block_number,
            'timestamp': timestamp_info['timestamp'],
            'date': timestamp_info['date'],
            'token': (Uniswap_v3_Pools_TY_df['token1_address'][i]
                      if args['amount0'] == 0 else Uniswap_v3_Pools_TY_df['token0_address'][i]),
            'amount': args['amount1'] if args['amount0'] == 0 else args['amount0']
        }
        results.append(info)
    return results

# Parallel processing
num_processes = 64
manager = Manager()
Uniswap_v3_flashloan_logs = manager.list()

with Pool(num_processes) as pool:
    for result in tqdm(pool.imap(process_flash_logs, range(len(Uniswap_v3_Pools_TY_df))), total=len(Uniswap_v3_Pools_TY_df)):
        Uniswap_v3_flashloan_logs.extend(result)

print('initialise')
Uniswap_v3_flashloan_logs = list(Uniswap_v3_flashloan_logs)

print('to df')
Uniswap_v3_flashloan_logs_df = pd.DataFrame(Uniswap_v3_flashloan_logs).sort_values(by='timestamp').reset_index(drop=True)
min_indices = Uniswap_v3_flashloan_logs_df.groupby('tx_hash')['logIndex'].idxmin()
Uniswap_v3_flashloan_unique_df = Uniswap_v3_flashloan_logs_df.loc[min_indices].sort_values(by='timestamp').reset_index(drop=True)
Uniswap_v3_flashloan_count_df = Uniswap_v3_flashloan_unique_df.groupby('date').size().reset_index(name='daily_flash_tx_count')

print('to_csv')
Uniswap_v3_flashloan_logs_df.to_csv('/local/scratch/exported/MP_Defi_txs_TY_23/guanda/Uniswap_v3_flashloan_logs.csv', index=False)
Uniswap_v3_flashloan_unique_df.to_csv('/local/scratch/exported/MP_Defi_txs_TY_23/guanda/Uniswap_v3_flashloan_unique.csv', index=False)
Uniswap_v3_flashloan_count_df.to_csv('/local/scratch/exported/MP_Defi_txs_TY_23/guanda/Uniswap_v3_flashloan_count.csv', index=False)
